import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { initializeApp } from 'firebase/app';
import { getAuth } from 'firebase/auth';

// Import components and pages
import Header from './components/layout/Header';
import Footer from './components/layout/Footer';
import Dashboard from './pages/Dashboard';
import Generate from './pages/Generate';
import Login from './pages/Login';
import QuestionBanks from './pages/QuestionBanks';
import CreatePaper from './pages/CreatePaper';
import Profile from './pages/Profile';
import { AuthProvider } from './context/AuthContext';

// --- Firebase Configuration ---
// It's recommended to move this to a separate file (e.g., services/firebase.ts)
// and import it here. The config values should come from environment variables.
const firebaseConfig = {
  apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
  authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN,
  projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID,
  storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET,
  messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
  appId: process.env.REACT_APP_FIREBASE_APP_ID,
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
export const auth = getAuth(app);

const App: React.FC = () => {
  useEffect(() => {
    // Log project ID to confirm Firebase is initialized
    if(firebaseConfig.projectId) {
      console.log(`Firebase initialized for project: ${firebaseConfig.projectId}`);
    } else {
      console.error("Firebase config is missing. Make sure .env.development or .env.production is set up correctly.");
    }
  }, []);

  return (
    <AuthProvider>
      <Router>
        <div className="flex flex-col min-h-screen bg-gray-50 font-sans">
          <Header />
          <main className="flex-grow container mx-auto px-4 py-8">
            <Routes>
              <Route path="/" element={<Dashboard />} />
              <Route path="/generate" element={<Generate />} />
              <Route path="/login" element={<Login />} />
              <Route path="/banks" element={<QuestionBanks />} />
              <Route path="/create-paper" element={<CreatePaper />} />
              <Route path="/profile" element={<Profile />} />
            </Routes>
          </main>
          <Footer />
        </div>
      </Router>
    </AuthProvider>
  );
};

export default App;
